import React,{ Component} from 'react'
import { Table,Checkbox,Button} from 'semantic-ui-react';
import showRows from './showRows';
import '../App.css' 




export default class showRequest extends Component {
  static async getInitialProps(props) {
  let f=await props.contract.methods.getreqNo().call()
  const info = await Promise.all(
    Array(f)
      .fill()
      .map((element, index) => {
        return props.contract.methods.getRequeststatus(index).call();
      })
      
  )
  return info
    }
  
     componentDidMount=()=>{
         this.props.info.map((request, index) => {
          return (
            <showRows
             info={this.props.info}
            />
          );
        });
      


     }
    componentDidUpdate=(prevProps)=>{
      if(prevProps.refresh != this.props.refresh)
      {

         this.props.info.map((request, index) => {
          return (
            <showRows
             info={this.props.info}
            />
          );
        });
      }
    }
    

 
 render(){
  
  return (
    <div className="ui container">
      {console.table(this.props.info)}

    <Table compact celled definition>
  <Table.Header>
    <Table.Row>
      <Table.HeaderCell />
      <Table.HeaderCell>Request_NO</Table.HeaderCell>
      <Table.HeaderCell>Creator</Table.HeaderCell>
      <Table.HeaderCell>Description:</Table.HeaderCell>
      <Table.HeaderCell>Amount:</Table.HeaderCell>
      <Table.HeaderCell>Recipient:</Table.HeaderCell>
      <Table.HeaderCell>Voters/Donors</Table.HeaderCell>
      <Table.HeaderCell>Payment Completed</Table.HeaderCell>
     
    </Table.Row>
  </Table.Header>
    </Table>

 </div> 
  )
 }
}
