import React, { useState } from 'react';
import { PdfUpload } from 'react-ipfs-uploader';
import 'semantic-ui-css/'
import 'semantic-ui-react'


 const Tender=(props) =>{
  const [pdfUrl, setPdfUrl] = useState('')
  const [target, setTarget] = useState('')
  const [min, setMin] = useState('')
  const [alertMsg, setmsg] = useState(false)
  

 
  const changeHandler=(e)=>{
    setTarget(e.target.value);
    setMin(e.target.value);
  }

  const submitHandler = async() => {
    alert('Are you sure to deploy your tender ?');
    await props.contract.methods.registerTender(target,min,pdfUrl).send({from:props.account})
    setTarget(" ");
    setMin(" ");
    setmsg(true)

  }
  return (
    <div className="ui container center">
      <h2> Tender GENERATION</h2>
      <form className="ui form">
      
        <div className="field">
          <label>Target</label>
          <input type="number" name="Target" placeholder="Target" onChange={changeHandler}></input>
        </div>
        <div className="field">
          <label>Minimum Contribution</label>
          <input type="number" name="Minimum Contribution" placeholder="Minimum Contribution" onChange={changeHandler}></input>
        </div>
      </form>
      <p>Upload Your Protocol Pdf</p>
      <PdfUpload setUrl={setPdfUrl} />
      <button className='ui button blue' onClick={submitHandler}>Create Tender</button>
        {
           alertMsg?
            <div class="ui success message">
            <i class="close icon"></i>
             <p>Your user registration was successful.</p>
             </div>
             :
             console.log('')
         }
         </div>
  
  );
}
export default Tender
