import React,{ useEffect, useRef, useState} from 'react'
import { Table,Checkbox,Button} from 'semantic-ui-react';
import '../App.css'


const ShowRequest=(props)=> {
  const[cAddress,setCaddress]=useState(null)
  const[description,setDes]=useState(null)
  const[amount,setAmount]=useState(null)
  const[recipient,setRecp]=useState(null)
  const[donors,setDonors]=useState(null)
  const[voters,setVoters]=useState(null)
  const[completed,setCom]=useState(null)
  const isInitialMount=useRef(true);




const requestTemplate={reqNo:"",cAddress:"",description:"",amount:"",donors:"",voters:"",completed:""}
const [Requests,setRequests]=useState([])

const requestInfo=async()=>{
  const info = await props.contract.methods.getRequeststatus(props.reqNumber).call();
  setCaddress(info[6])
  setDes(info[5])
  setAmount(props.web3.utils.fromWei(info[1]))
  setCom(info[0])
 setVoters(info[3])
 setDonors(info[4])
 setRecp(info[2])
 setRequests([info])
 setRequests([...Requests,requestTemplate])
}

useEffect(()=>{
  console.log("inside useEffect")
  if(isInitialMount.current){
    isInitialMount.current=false;
  }else{
    requestInfo(); 
  }
},[props.refresh])

 

  return (
    <div className="ui container">
    <Table compact celled definition>
  <Table.Header>
    <Table.Row>
      <Table.HeaderCell />
      <Table.HeaderCell>Request_NO</Table.HeaderCell>
      <Table.HeaderCell>Creator</Table.HeaderCell>
      <Table.HeaderCell>Description:</Table.HeaderCell>
      <Table.HeaderCell>Amount:</Table.HeaderCell>
      <Table.HeaderCell>Recipient:</Table.HeaderCell>
      <Table.HeaderCell>Voters/Donors</Table.HeaderCell>
      <Table.HeaderCell>Payment Completed</Table.HeaderCell>
     
    </Table.Row>
  </Table.Header>
   

   {Requests.map((request,index) => (
      <Table.Body>
      <Table.Row key={index}>
        <Table.Cell collapsing>
          <Checkbox slider />
        </Table.Cell>
        <Table.Cell>{props.reqNumber}</Table.Cell>
        <Table.Cell>{cAddress}</Table.Cell>
        <Table.Cell>{description}</Table.Cell>
        <Table.Cell>{amount}</Table.Cell>
        <Table.Cell>{recipient}</Table.Cell>
        <Table.Cell>{voters}/{donors}</Table.Cell>
      <Table.HeaderCell>{completed}</Table.HeaderCell>

        
      </Table.Row>
      </Table.Body>
      

   )
   )}
    <Table.Footer fullWidth>
    <Table.Row>
      <Table.HeaderCell />
      <Table.HeaderCell colSpan='7'>
        <Button
          floated='right'
          icon
          labelPosition='left'
          primary
          size='small'
        >
        Make Payment
        </Button>
        <Button size='small' color="green">Vote</Button>
      </Table.HeaderCell>
    </Table.Row>
  </Table.Footer>

  </Table>

 </div> 
  )
}
export default ShowRequest
