import React, { Component } from 'react';
import {Segment,  Card} from 'semantic-ui-react';
import {Link} from "react-router-dom"


export default class showDeployedTender extends Component {
    constructor(props) {
      super(props);
    }
 
  state={
    target: null,
    minimumcontribution: null,
    url: null,
    beneficiary: null,
    balance : null,
    donors : null
  }
  prevState=this.state.target

  componentDidUpdate = async({prevState}) =>{
    if (prevState == this.state.target) {
    const info = await this.props.contract.methods.getTenderinfo().call();
    this.setState(
      {minimumcontribution : info[0],target : info[1],url : info[2], balance : info[3], beneficiary: info[4],donors:info[5]});
    }
   console.log("target=",this.state.target)
  }
  render() {
    return (
      <div className='ui container'>
     {console.log(this.props.contract)}
      {console.log(this.props.account)}
      <Card.Group>
      <Card>
        <Card.Content
          header={this.props.address}
          meta='Deployed Tender Address'
          description='This is the address of deployed tender in this system.'
        />
      </Card>
      <Card>
        <Card.Content>
          <Card.Header>{this.state.beneficiary}</Card.Header>
          <Card.Meta>Address of the creator</Card.Meta>
          <Card.Description>
            This address can create request for collected amount
          </Card.Description>
        </Card.Content>
      </Card>
      <Card>
        <Card.Content>
          <Card.Header>{this.state.target}</Card.Header>
          <Card.Meta content='Target Amount' />
          <Card.Description content='This is the amount targeted for this tender.' />
        </Card.Content>
      </Card>
  
      <Card>
        <Card.Content
          header={this.state.minimumcontribution}
          meta='Minimum Contribution'
          description='This is the minimum amount specified by tender to donate.'
        />
      </Card>
  
      <Card
        header={this.state.url}
        meta='Link of the protocol'
        description='This is the uploaded protocol from this tender.Copy URL and review it.'
      />
    </Card.Group>
    <Segment.Group horizontal>
      <Segment>Tender Balance: {this.state.balance}</Segment>
    <Segment>Number Of Donors: {this.state.donors} </Segment>
    <Segment>Number of Requests:   <Link to='/dashboard/request'>View Requests</Link></Segment>
  
  </Segment.Group>
  <div className="field">
          <label>Amount:</label>
          <input type="number" placeholder='Amount'></input>
        </div>

        <button className='ui button green' >Donate</button>
  
       </div>
    );
  }
}
