import React, { Component } from 'react';
import {  Card} from 'semantic-ui-react';
import 'semantic-ui-react';
import '../App.css'

export default class Donation extends Component {
  state={
    target: null,
    minimumcontribution: null,
    url: null,
  }

  tenderInfo = async() =>{
    const info = await this.props.contract.methods.getTenderinfo().call();
    this.setState(
      {minimumcontribution : info[0],target : info[1],url : info[2]}
      )
      console.log("target=",this.state.target)
  }
  render() {
    return (
      <div className='ui container'>
      {this.tenderInfo}
      <Card.Group>
      <Card>
        <Card.Content>
          <Card.Header>Tender Creator</Card.Header>
          <Card.Meta>Address of the creator</Card.Meta>
          <Card.Description>
            This address can create request for collected amount
          </Card.Description>
        </Card.Content>
      </Card>
      <Card>
        <Card.Content>
          <Card.Header>{this.state.target}</Card.Header>
          <Card.Meta content='Amount expected to reach' />
          <Card.Description content='This is the amount targeted for this tender.' />
        </Card.Content>
      </Card>
  
      <Card>
        <Card.Content
          header='Minimum Contribution'
          meta='Minimum Amount'
          description='This is the minimum amount specified by tender to donate.'
        />
      </Card>
  
      <Card
        header='Protocol Url'
        meta='Link of the protocol'
        description='This is the uploaded protocol from this tender. Review it'
      />
    </Card.Group>
  
       </div>
    );
  }
}
