import React,{ useState,useEffect, useRef} from 'react'
import { Table,Button,Input,Segment} from 'semantic-ui-react';
import TransactionRow from './TransactionRow';
import '../App.css' 




const ShowRequest=(props)=> {
  const[current,setCurrent]=useState(0) //dummy state
   const datas=JSON.parse(localStorage.getItem("info" || "[]"))

  const initialInfo=async()=>{

  let f=await props.contract.methods.getreqNo().call()
  const info = await Promise.all(
    Array(parseInt(f)).fill().map((element, index) => {
        return props.contract.methods.getRequeststatus(index).call();
      })
  )
localStorage.setItem("info",JSON.stringify(info))

    }


    useEffect(()=>{

      console.log("inside useeffect")
      initialInfo();
      handler();
      
    })

  useEffect(()=>{

    console.log("inside useeffect")
    initialInfo();
    handler();
    
  },[props.refresh])
  

const handler=()=>{
  setTimeout(()=>{
    setCurrent(!current)
  },1000)
}

  return (
    <div className='ui conatiner center'>
      {current}
      {console.log("render")}


    <Table compact celled definition >
  <Table.Header classNmae='ui center'>
    <Table.Row>
      <Table.HeaderCell />
      <Table.HeaderCell>Request_NO</Table.HeaderCell>
      <Table.HeaderCell>Creator</Table.HeaderCell>
      <Table.HeaderCell>Description:</Table.HeaderCell>
      <Table.HeaderCell>Amount:</Table.HeaderCell>
      <Table.HeaderCell>Recipient:</Table.HeaderCell>
      <Table.HeaderCell>Voters/Donors</Table.HeaderCell>
      <Table.HeaderCell>Payment Completed</Table.HeaderCell>
     
    </Table.Row>
  </Table.Header>

  <TransactionRow
         account={props.account}
         contract={props.contract}
         web3={props.web3}
         hadnler={handler}
         />


   

  
  
  </Table>
  

 </div> 
  )
}
export default ShowRequest
