import React, { Component } from 'react';
import {Segment,  Card} from 'semantic-ui-react';
import {Link} from "react-router-dom"


export default class showDeployedTender extends Component {
    constructor(props) {
      super(props);
    }
 
  state={
    target: null,
    minimumcontribution: null,
    url: null,
    beneficiary: null,
    balance : null,
    donors : null,
    requests : null,
    raisedAmount:null,
    donateValue:null

  }
  prevState=this.state.target;

  componentDidUpdate = async({prevState}) =>{
    if (prevState == this.state.target) {
    const info = await this.props.contract.methods.getTenderinfo().call();
    this.setState(
      {minimumcontribution : info[0],target : info[1],url : info[2], balance : info[3], 
        beneficiary: info[4],donors:info[5],requests : info[6],raisedAmount :info[7] 
      });
    }
    
  }

   clickHandler = async() => {
    
  await this.props.contract.methods.donate().send({from: this.props.account,value: this.state.props.web3.utils.toWei(this.state.donateValue)});
  console.log("dontvalue=",this.state.donateValue)
}



  render() {
    return (
      <div className='ui container center'>
     {console.log(this.state.donateValue)}
      <h1>Tender Status:</h1>
      <Card.Group>
      <Card>
        <Card.Content
          header={this.props.address}
          meta='Deployed Tender Address'
          description='This is the address of deployed tender in this system.'
        />
      </Card>
      <Card>
        <Card.Content>
          <Card.Header>{this.state.beneficiary}</Card.Header>
          <Card.Meta>Address of the creator</Card.Meta>
          <Card.Description>
            This address can create request for collected amount
          </Card.Description>
        </Card.Content>
      </Card>
      <Card
        header={this.state.url}
        meta='Link of the protocol'
        description='This is the uploaded protocol from this tender.Copy URL and review it.'
      />
      <Card>
        <Card.Content>
          <Card.Header>{this.state.target}</Card.Header>
          <Card.Meta content='Target Amount' />
          <Card.Description content='This is the amount targeted for this tender.' />
        </Card.Content>
      </Card>
  
      <Card>
        <Card.Content
          header={this.state.minimumcontribution}
          meta='Minimum Contribution'
          description='This is the minimum amount specified by tender to donate.'
        />
      </Card>
  
      <Card
        header={this.state.raisedAmount}
        meta='Raised Amount'
        description='This is the collected amount in this tender.'
      />
       <Card
        header={this.state.donors}
        meta='Number Of Donors'
        description='This shows the number of people who donated in this tender.'
      />

      <Card
        header={this.state.requests}
        meta='Number Of Requests'
        description="This shows the number of requests created by the creator after he/she wished to stop funding.You won't be able to donate after creator makes request."
      />
    </Card.Group>
    <Segment.Group horizontal>
      <Segment>Tender Current Balance: {this.state.balance}</Segment>
    <Segment>Number of Requests:   <Link to='/dashboard/request'>View Requests</Link></Segment>
  
  </Segment.Group>
  <div className="field">
          <label>Amount:</label>
          <input type="number" placeholder='Amount' onChange={(e) => this.setState({ donateValue: e.target.value })}></input>
        </div>

        <button className='ui button green' onClick={this.state.clickHandler} 
        >Donate</button>
  
       </div>
    );
  }
}
