import React, { useState } from 'react';
import {create} from "ipfs-http-client"
const client=ipfsHtppClient('https://ipfs.infura.io:5001/api.v0')

export default function Tender() {
  const [fileUrl, setFileUrl] = useState('');
  async function onChange(e) {
    const file=e.target.files[0]
    try{
      const added=await client.add(file);
      const url='https://ipfs.infura.io/ipfs/${added.path}'
      setFileUrl(url);
    }
    catch(error){
      console.log('error uploading file',error);
    }
  }
  return (
    <div className="ui container center">
      <h2> Tender GENERATION</h2>
      <form className="ui form">
        <div className="field">
          <label>Deadline</label>
          <input type="number" name="Deadline" placeholder="Deadline"></input>
        </div>
        <div className="field">
          <label>Target</label>
          <input type="number" name="Target" placeholder="Target"></input>
        </div>
        <div className="field">
          <label>Minimum Contribution</label>
          <input type="number" name="Minimum Contribution" placeholder="Minimum Contribution"></input>
        
          <p>Upload protocol for Tender request</p>

         <input type='file' onChange={onChange} />
         {fileUrl &&(<img src={fileUrl} width="60px" />)}        
        </div>
        
      </form>
     
    </div>
  );
}
