import React, { Component } from 'react';
import {Segment,  Card} from 'semantic-ui-react';
import 'semantic-ui-react';
import '../App.css'

export default class Donation extends Component {
  state={
    target: null,
    minimumcontribution: null,
    url: null,
    beneficiary: null,
    balance : null
  }

  tenderInfo = async() =>{
    const info = await this.props.contract.methods.getTenderinfo().call();
    this.setState(
      {minimumcontribution : info[0],target : info[1],url : info[2], balance : info[3], beneficiary: info[4]});
    const {minimumcontribution,target,url,balance,beneficiary}=this.state;
    console.log(minimumcontribution);
    console.log(target);
    console.log(url);
    console.log(beneficiary);
    console.log(balance);

  }
  render() {
    return (
      <div className='ui container'>
      <button onClick={this.tenderInfo}>clickme</button>
      <Card.Group>
      <Card>
        <Card.Content>
          <Card.Header>{this.state.beneficiary}</Card.Header>
          <Card.Meta>Address of the creator</Card.Meta>
          <Card.Description>
            This address can create request for collected amount
          </Card.Description>
        </Card.Content>
      </Card>
      <Card>
        <Card.Content>
          <Card.Header>{this.state.target}</Card.Header>
          <Card.Meta content='Target Amount' />
          <Card.Description content='This is the amount targeted for this tender.' />
        </Card.Content>
      </Card>
  
      <Card>
        <Card.Content
          header={this.state.minimumcontribution}
          meta='Minimum Contribution'
          description='This is the minimum amount specified by tender to donate.'
        />
      </Card>
  
      <Card
        header={this.state.url}
        meta='Link of the protocol'
        description='This is the uploaded protocol from this tender.Copy URL and review it.'
      />
    </Card.Group>
    <Segment.Group horizontal>
      <Segment>Tender Balance: {this.state.balance}</Segment>
    <Segment>Number Of Donors:</Segment>
    <Segment>Number of Requests</Segment>

  
  </Segment.Group>
  <div className="field">
          <label>Amount:</label>
          <input type="number" placeholder='Amount'></input>
        </div>

        <button className='ui button green' >Donate</button>
  
       </div>
    );
  }
}
