import React, { Component } from 'react';
import 'semantic-ui-css/semantic.min.css';
import {FileUpload} from 'react-ipfs-uploader'


class Tender extends Component {
    state = { fileUrl:''};

  
  render() {
    return (
    <div className="ui container center">
      <h2> Tender GENERATION</h2>
      <form className="ui form">
        <div className="field">
          <label>Deadline</label>
          <input type="number" name="Deadline" placeholder="Deadline"></input>
        </div>
        <div className="field">
          <label>Target</label>
          <input type="number" name="Target" placeholder="Target"></input>
        </div>
        <div className="field">
          <label>Minimum Contribution</label>
          <input type="number" name="Minimum Contribution" placeholder="Minimum Contribution"></input>
        
          <p>Upload protocol for Tender request</p>
        
          <FileUpload setUrl={this.setState({fileUrl})} />
            FileUrl : <a
                href={fileUrl}
                target='_blank'
                rel='noopener noreferrer'
            >
                {this.state.fileUrl}
            </a>
        </div>
        
      </form>
     
    </div>
    );
  }
}
export default Tender
