import React,{ useEffect} from 'react'
import { Table} from 'semantic-ui-react';
import '../App.css' 
import DisplayRows from './DisplayRow';


const ShowRequest=(props)=> {

  const initialInfo=async()=>{

  let f=await props.contract.methods.getreqNo().call()
  const info = await Promise.all(
    Array(parseInt(f)).fill().map((element, index) => {
        return props.contract.methods.getRequeststatus(index).call();
      })
  )

{
  
 <DisplayRows>
   infos={info}
 </DisplayRows>

}
console.log('inside initialinfo')
 }


  useEffect(async()=>{

    initialInfo();
  },[props.refresh])
  


  return (
    <div className="ui container">
    <Table compact celled definition>
  <Table.Header>
    <Table.Row>
      <Table.HeaderCell />
      <Table.HeaderCell>Request_NO</Table.HeaderCell>
      <Table.HeaderCell>Creator</Table.HeaderCell>
      <Table.HeaderCell>Description:</Table.HeaderCell>
      <Table.HeaderCell>Amount:</Table.HeaderCell>
      <Table.HeaderCell>Recipient:</Table.HeaderCell>
      <Table.HeaderCell>Voters/Donors</Table.HeaderCell>
      <Table.HeaderCell>Payment Completed</Table.HeaderCell>
     
    </Table.Row>
  </Table.Header>
 
  </Table>

 </div> 
  )
}
export default ShowRequest
