import React, { Component } from "react";
import Authentication from "./contracts/Authentication.json";
import getWeb3 from "./getWeb3";

import "./App.css";

class App extends Component {
  state = { cid: null,address: null, web3: null, accounts: null, contract: null };

  componentDidMount = async () => {
    try {
      // Get network provider and web3 instance.
      const web3 = await getWeb3();

      // Use web3 to get the user's accounts.
      const accounts = await web3.eth.getAccounts();

      // Get the contract instance.
      const networkId = await web3.eth.net.getId();
      const deployedNetwork = Authentication.networks[networkId];
      const contract1 = new web3.eth.Contract(
        Authentication.abi,
        deployedNetwork && deployedNetwork.address,
      );

      // Set web3, accounts, and contract to the state, and then proceed with an
      // example of interacting with the contract's methods.
      this.setState({ web3, accounts, contract: contract1 });
    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Failed to load web3, accounts, or contract. Check console for details.`,
      );
      console.error(error);
    }
  };

  handleChange=(e)=>{
    this.setState({cid : e.target.value})
  }

  runExample = async () => {
    const { accounts, contract } = this.state;
    await contract.methods.register(this.state.cid).send({from: accounts[0]});
    const cid1=await contract.methods.getSignatureHash().call();
    this.setState({ cid: cid1 });
  };

  runExample2 = async () => {
    const { accounts, contract } = this.state;
    const addressq=await contract.methods.getSignatureHash().call();
    this.setState({address : addressq} );
  };


  render() {
    if (!this.state.web3) {
      return <div>Loading Web3, accounts, and contract...</div>;
    }
    return (
      <div className="App">
        <h1>Good to Go!</h1>
        <p>Your Truffle Box is installed and ready.</p>
        <h2>Smart Contract Example</h2>
        <input value={this.state.cid} onChange={this.handleChange}></input>
       <br></br> <button onClick={this.runExample}>storeCID</button>
        <div>The returned user address from authentication contract  is: {this.state.address} </div>
      </div>
    );
  }
}

export default App;
