import React, { useState,useEffect} from 'react';
import swal from 'sweetalert';
import { Table,Checkbox } from 'semantic-ui-react';





 const Tender=() =>{
  const [des,setDes] = useState('')
  
  const [amount, setAmount] = useState('')
  const [Recp, setRecp] = useState('')
  const [ReqNo, setReqno] = useState('')
  const [cAdd, setCadd] = useState('')
  const [voters, setVoters] = useState('')
  const [donors, setDonors] = useState('')
  const [addStatus,setAdd] = useState(false)
 

  

useEffect((props)=>{

},[addStatus])


const clickHandler =()=>{
setAdd(!addStatus)
console.log("add status",addStatus)
}
  

  // }
  return (
    <div className="ui container center">
      {console.log("descriptop",des)}
      {console.log("damount",amount)}
      {console.log("address",Recp)}
      {console.log("add status",addStatus)}
      <h2>REQUEST CREATION </h2>
      <form className="ui form" type='submit'>
      
        <div className="field">
          <label>Description:</label>
          <input type="text" name="Target" placeholder="Description" onChange={(e) => setDes(e.target.value)}></input>
        </div>
        <div className="field">
          <label>Amount:</label>
          <input type="number"  placeholder="Request Amount" onChange={(e) => setAmount(e.target.value)}></input>
        </div>
        <div className="field">
          <label>Recipient:</label>
          <input type="text" name="Minimum Contribution" placeholder="Payable Address" onChange={(e) => setRecp(e.target.value)}></input>
        </div>
      </form>
      
      <p></p>
      <button className='ui button blue' onClick={clickHandler} >Create Request</button>
      <p></p>
      <h2>Request Info:</h2>
      <Table celled compact definition>
    <Table.Header fullWidth>
      <Table.Row>
        <Table.HeaderCell />
        <Table.HeaderCell>Request_No:</Table.HeaderCell>
        <Table.HeaderCell>Creator Address</Table.HeaderCell>
        <Table.HeaderCell>Description</Table.HeaderCell>
        <Table.HeaderCell>Amount</Table.HeaderCell>
        <Table.HeaderCell>Recipient</Table.HeaderCell>
        <Table.HeaderCell>Voters/Donors</Table.HeaderCell>
      </Table.Row>
    </Table.Header>
    </Table>

    {ReqNo.map((requestNumber,index)=>{
         <Table.Body>
         <Table.Row>
           <Table.Cell collapsing>
             <Checkbox toggle />
           </Table.Cell>
           <Table.Cell>ReqNo</Table.Cell>
           <Table.Cell>cAdd</Table.Cell>
           <Table.Cell>des</Table.Cell>
           <Table.Cell>amount</Table.Cell>
           <Table.Cell>Recp</Table.Cell>
           <Table.Cell>voter/donors</Table.Cell>
         </Table.Row>
       </Table.Body>
    })}

        
         </div>
  
  );
}
export default Tender
