import React, { useState,useEffect} from 'react';
import swal from 'sweetalert';
import { Table,Checkbox } from 'semantic-ui-react';





 const RequestLog=() =>{
   const[reqNo,setReq]=useState("")
   const[cAddress,setCaddress]=useState("")
   const[description,setDes]=useState("")
   const[amount,setAmount]=useState("")
   const[recipient,setRecp]=useState("")
   const[donors,setDonors]=useState("")
   const[voters,setVoters]=useState("")
   

   const reqInfo={
     reqNO:"",
     cAddress:"",
     description:"",
     amount:"",
     recipient:"",
     donors:"",
     voters:""
  }
  const[Requests,setRequests]=useState([reqInfo])


  


  return (
    <div className="ui container center">
     
      <h2>REQUEST CREATION </h2>
      <form className="ui form" type='submit'>
      
        <div className="field">
          <label>Description:</label>
          <input type="text" name="Target" placeholder="Description" onChange={(e) => setDes(e.target.value)}></input>
        </div>
        <div className="field">
          <label>Amount:</label>
          <input type="number"  placeholder="Request Amount" onChange={(e) => setAmount(e.target.value)}></input>
        </div>
        <div className="field">
          <label>Recipient:</label>
          <input type="text" name="Minimum Contribution" placeholder="Payable Address" onChange={(e) => setRecp(e.target.value)}></input>
        </div>
      </form>
      
      <p></p>
      <button className='ui button blue' >Create Request</button>
      <p></p>
      <h2>Request Info:</h2>
      <Table compact celled definition>
    <Table.Header>
      <Table.Row>
        <Table.HeaderCell />
        <Table.HeaderCell>Name</Table.HeaderCell>
        <Table.HeaderCell>Name</Table.HeaderCell>
        <Table.HeaderCell>Name</Table.HeaderCell>
        <Table.HeaderCell>Name</Table.HeaderCell>
        <Table.HeaderCell>Registration Date</Table.HeaderCell>
        <Table.HeaderCell>E-mail address</Table.HeaderCell>
        <Table.HeaderCell>Premium Plan</Table.HeaderCell>
      </Table.Row>
    </Table.Header>
    </Table>

   

    


        
         </div>
  
  );
}
export default RequestLog
