import React,{useState,useEffect} from 'react'
import {Segment , Card} from 'semantic-ui-react';
import {Link} from "react-router-dom"


export default function TenderLogs(props) {
  const [pdfUrl ,setPdfUrl] = useState('')
  const [target ,setTarget] = useState('')
  const [min ,setMin] = useState('')
  const [donors ,setDonors] = useState('')
  const [beneficiary ,setBeneficiary] = useState('')
  const [raisedAmount ,setRaisedamount] = useState('')
  const [request ,setRequest] = useState('')
  const [balance, setBalance] = useState('')

  weiTOeth=(amount)=>{
    const ConvertedAmount=props.web3.utils.fromWei(amount);
    return ConvertedAmount;
    }

    ethTOwei=(amount)=>{
      const donateAmount=props.web3.utils.toWei(valueINether);
    }
 
  useEffect(async()=>{

        const info = await props.contract.methods.getTenderinfo().call();
  
        setMin(weiTOeth(info[0]))
        setTarget(weiTOeth(info[1]))
        setPdfUrl(info[2]) 
        setBalance(weiTOeth(info[3]))
        setBeneficiary(info[4])
        setDonors(info[5])
        setRequest(info[6])
        setRaisedamount(weiTOeth(info[7]))    
  }
        );


        clickHandler = async ()=>{
          await props.web3.eth.sendTransaction(
            {from : props.account,to:this.props.address, value:this.props.web3.utils.toWei('0.001','ether')})
      }
      
           
  return (
    <div className='ui container center'>
    {console.log(props.web3)}
     <h1>Tender Status:</h1>
     <Card.Group>
     <Card>
       <Card.Content
         header={props.address}
         meta='Deployed Tender Address'
         description='This is the address of deployed tender in this system.'
       />
     </Card>
     <Card>
       <Card.Content>
         <Card.Header>{beneficiary}</Card.Header>
         <Card.Meta>Address of the creator</Card.Meta>
         <Card.Description>
           This address can create request for collected amount
         </Card.Description>
       </Card.Content>
     </Card>
     <Card
       header={pdfUrl}
       meta='Link of the protocol'
       description='This is the uploaded protocol from this tender.Copy URL and review it.'
     />
     <Card>
       <Card.Content>
         <Card.Header>{target} ETH</Card.Header>
         <Card.Meta content='Target Amount' />
         <Card.Description content='This is the amount targeted for this tender.' />
       </Card.Content>
     </Card>
 
     <Card>
       <Card.Content>
         <Card.Header>{min} ETH</Card.Header>
         <Card.Meta content='Minimum Amount' />
         <Card.Description content='This is minimum amount specified by the tender to contribute.' />
       </Card.Content>
     </Card>
     <Card>
       <Card.Content>
         <Card.Header>{raisedAmount} ETH</Card.Header>
         <Card.Meta content='Raised Amount' />
         <Card.Description content='This is the collected amount in this tender.' />
       </Card.Content>
     </Card>

 
      <Card
       header={donors}
       meta='Number Of Donors'
       description='This shows the number of people who donated in this tender.'
     />

     <Card
       header={request}
       meta='Number Of Requests'
       description="This shows the number of requests created by the creator after he/she wished to stop funding.You won't be able to donate after creator makes request."
     />
   </Card.Group>
   <Segment.Group horizontal>
     <Segment>Tender Current Balance: {balance} ETH</Segment>
   <Segment>Number of Requests:   <Link to='/dashboard/request'>View Requests</Link></Segment>
 
 </Segment.Group>

 
      </div>
  )
}
