import React,{useState} from 'react'

export default function donateform() {
    const[value,setValue]=useState('')

    clickHandler = async ()=>{
      await props.contract.methods.donate().send(
        {from : props.account,value:props.web3.utils.toWei(value)});
    
}
    
  return (
    <div>
        <label>Amount(ETH)</label>
        <input type="number" placeholder='donate amount' onChange={e=>setValue(e.target.value)}></input>
        <button className="ui button blue">Donate</button>
        </div>
  )
}
