import React, { Component } from "react";
import Authentication from "./contracts/Authentication.json";
import getWeb3 from "./getWeb3";
import 'semantic-ui-css/semantic.min.css';

import "./App.css";

class App extends Component {
  state = { cid: null,address: null, web3: null, accounts: null, contract: null };

  componentDidMount = async () => {
    try {
      // Get network provider and web3 instance.
      const web3 = await getWeb3();

      // Use web3 to get the user's accounts.
      const accounts = await web3.eth.getAccounts();

      // Get the contract instance.
      const networkId = await web3.eth.net.getId();
      const deployedNetwork = Authentication.networks[networkId];
      const contract1 = new web3.eth.Contract(
        Authentication.abi,
        deployedNetwork && deployedNetwork.address,
      );

      // Set web3, accounts, and contract to the state, and then proceed with an
      // example of interacting with the contract's methods.
      this.setState({ web3, accounts, contract: contract1 });
    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Failed to load web3, accounts, or contract. Check console for details.`,
      );
      console.error(error);
    }
  };


  render() {
    if (!this.state.web3) {
      return <div>Loading Web3, accounts, and contract...</div>;
    }
    return (
      <div className="ui container center">
      <h2> TENDER GENERATION</h2>
      <form className="ui form">
        <div className="field">
          <label>Deadline</label>
          <input type="number" name="Deadline" placeholder="Deadline"></input>
        </div>
        <div className="field">
          <label>Target</label>
          <input type="number" name="Target" placeholder="Target"></input>
        </div>
        <div className="field">
          <label>Minimum Contribution</label>
          <input type="number" name="Minimum Contribution" placeholder="Minimum Contribution"></input>
          <br>
          <p>Upload protocol for tender request</p>
          </br>
          <button className='ui button blue'>File Upload</button>
        </div>

      </form>

    </div>
    );
  }
}

export default App;
