import React,{useState}from 'react'
import { Button,Input } from 'semantic-ui-react'

const TransactionRow=(props)=> {
    const a=0;
    const[reqNumber,setReq]=useState(null)


    const VoteHandler = async () => {
        await props.contract.methods.voteRequest(reqNumber).send({
          from: props.account[0]


        });
        props.handler(a)
    
      };
  return (
    <div>
        {console.log(props.contract)}
        {console.log("account",props.account)}
        <Button
          floated='right'
          icon
          labelPosition='left'
          primary
          size='small'
        >
         
        Make Payment
        </Button >

        <Input inverted placeholder='Request Number' onChange={e=>setReq(e.target.value)} />    
        <p></p>
        <Button size='small' color="green" onClick={VoteHandler}>Vote</Button>
    </div>
  )
}
export default TransactionRow
