import React, { useState } from 'react';
import { PdfUpload } from 'react-ipfs-uploader';


 const Tender=(props) =>{
  const [pdfUrl, setPdfUrl] = useState('')
  const [target, setTarget] = useState('')
  const [min, setMin] = useState('')

 
  const changeHandler=(e)=>{
    setTarget(e.target.value)
    setMin(e.target.value)

  }

  const submitHandler = async() => {
    console.log("contract",props.contract)
    await props.contract.methods.registerTender(target,min,pdfUrl).send({from:props.account[0]})

  }
  return (
    <div className="ui container center">
      <h2> Tender GENERATION</h2>
      <form className="ui form">
      
        <div className="field">
          <label>Target</label>
          <input type="number" name="Target" placeholder="Target" onChange={changeHandler}></input>
        </div>
        <div className="field">
          <label>Minimum Contribution</label>
          <input type="number" name="Minimum Contribution" placeholder="Minimum Contribution" onChange={changeHandler}></input>
          <p>Upload protocol for Tender request</p>

          
        </div>
      </form>
      <PdfUpload setUrl={setPdfUrl} />
      <button className='ui button blue' onClick={submitHandler}>Submit</button>
    </div>
  );
}
export default Tender
