import React, { useState } from 'react';
import { PdfUpload } from 'react-ipfs-uploader';
import swal from 'sweetalert';




 const Tender=(props) =>{
  const [pdfUrl, setPdfUrl] = useState('')
  const [target, setTarget] = useState('')
  const [min, setMin] = useState('')
  


  const submitHandler = async() => {
    alert('Are you sure to deploy your tender ?');
    const targetinWEI=props.web3.utils.toWei(target);
    const mininWEI=props.web3.utils.toWei(min);
    await props.contract.methods.registerTender(targetinWEI,mininWEI,pdfUrl).send({from:props.account[0]})
    swal({
      title: "Good job!",
      text: "successfuly created!",
      icon: "success",
      button: "Continue To Website",
    });
    setMin(" ")
    setTarget(" ")
  

  }
  return (
    <div className="ui container center">
      {console.log(props.contract)}
      <h2>REQUEST CREATION </h2>
      <form className="ui form" type='submit'>
      
        <div className="field">
          <label>Description:</label>
          <input type="text" name="Target" placeholder="Description" onChange={(e) => setTarget(e.target.value)}></input>
        </div>
        <div className="field">
          <label>Amount:</label>
          <input type="number"  placeholder="Request Amount" onChange={(e) => setMin(e.target.value)}></input>
        </div>
        <div className="field">
          <label>Recipient:</label>
          <input type="text" name="Minimum Contribution" placeholder="Payable Address" onChange={(e) => setMin(e.target.value)}></input>
        </div>
      </form>
      
      <p></p>
      <button className='ui button blue' onClick={submitHandler} >Create Tender</button>
      {console.log("account=",props.account)}

        
         </div>
  
  );
}
export default Tender
