import React, { useState } from 'react';
import { PdfUpload } from 'react-ipfs-uploader';
import {useForm} from "react-hook-form";



 const Tender=() =>{
  const [pdfUrl, setPdfUrl] = useState('')
  const [target, setTarget] = useState('')
  const [minimum, setMinimum] = useState('')

  handlechange=(e) => {
    setMinimum(minimum =>[minimum,e.target.value])
    setTarget(target=>[target,e.target.value])
    setPdfUrl(pdfUrl=>[pdfUrl,pdfUrl])
  }
  const onSubmit = async() => {
    
   
  }
  return (
    <div className="ui container center">
      <h2> Tender GENERATION</h2>
      <form className="ui form">
        <div className="field">
          <label>Target</label>
          <input type="number" name="Target" placeholder="Target" onChange={handlechange}></input>
        </div>
        <div className="field">
          <label>Minimum Contribution</label>
          <input type="number" name="Minimum Contribution" placeholder="Minimum Contribution" onChange={handlechange}></input>
          <p>Upload protocol for Tender request</p>

          
        </div>
      
      </form>
      <PdfUpload setUrl={setPdfUrl}></PdfUpload>
      <button className='ui button blue' >Submit</button>
    </div>
  );
}
export default Tender
