import React, { Component } from 'react';
import { BrowserRouter as Router,Link,Switch,Route } from 'react-router-dom';
import Guideline from '../dashComponents/Guideline';
import Donation from '../dashComponents/Donation';
import Tender from '../dashComponents/Tender';
import '../dashComponents/sidebar.css';
import aboutUS from '../dashComponents/aboutUS';
import RequestLog from '../dashComponents/RequestLog';
import bcs from '../contracts/bcs.json'
import getWeb3 from '../getWeb3';

 class Dashboard extends Component {
     constructor(props) {
       super(props);
     }
    
    
  state = { sidebar : true}
    
 
      

    
  showSidebar = () => { 
     this.setState({sidebar: !(this.state.sidebar)});

  }
  render() {
    return( 
    <div>

    <Router>
    <nav className={this.state.sidebar ? "sidebar active" : "sidebar"}>
      <button className="hamburger" type="button" onClick={this.showSidebar}><div></div></button>
      <ul onClick={this.showSidebar}>
        <li>DASHBOARD</li>
        <li><Link to="/dashboard">User Guideline</Link></li>
        <li><Link to="/dashboard/tender">Tender Logs</Link></li>
        <li><Link to="/dashboard/donation">Donation Logs</Link></li>
        <li><Link to="/dashboard/request">Request Logs</Link></li>
        <li><Link to="/dashboard/aboutus">About Us</Link></li>
      </ul>
    </nav>
    

    <Switch>
      
      <Route path="/dashboard" exact component={Guideline} />

      <Route path="/dashboard/tender">
        <Tender 
         web3={this.props.web3}
         account={this.props.account}
         contract={this.props.contract} />
      </Route>

      <Route path="/dashboard/donation">
      <Donation
         web3={this.props.web3}
         account={this.props.account}
         contract={this.props.contract} />
      </Route>

      <Route path="/dashboard/request">
        <RequestLog 
        web3={this.props.web3}
        contract={this.props.contract}
        account={this.props.account}/>
      </Route>

      <Route path="/dashboard/aboutus"  component={aboutUS} />

    </Switch>
    </Router>

  </div>
    );
  }
}
export default Dashboard
