import React, { useState } from 'react';
import swal from 'sweetalert';
import {Link,BrowserRouter as Router,Switch,Route} from 'react-router-dom'
import showRequest from '../components/showRequest'





 const Tender=() =>{
  const [des,setDes] = useState('')
  const [amount, setAmount] = useState('')
  const [Recp, setRecp] = useState('')
  


  // const submitHandler = async() => {
  //   alert('Are you sure to deploy your tender ?');
  //   const targetinWEI=props.web3.utils.toWei(target);
  //   const mininWEI=props.web3.utils.toWei(min);
  //   await props.contract.methods.registerTender(targetinWEI,mininWEI,pdfUrl).send({from:props.account[0]})
  //   swal({
  //     title: "Good job!",
  //     text: "successfuly created!",
  //     icon: "success",
  //     button: "Continue To Website",
  //   });
  //   setMin(" ")
  //   setTarget(" ")
  

  // }
  return (
    <div className="ui container center">
      {console.log("descriptop",des)}
      {console.log("damount",amount)}
      {console.log("address",Recp)}
      <h2>REQUEST CREATION </h2>
      <form className="ui form" type='submit'>
      
        <div className="field">
          <label>Description:</label>
          <input type="text" name="Target" placeholder="Description" onChange={(e) => setDes(e.target.value)}></input>
        </div>
        <div className="field">
          <label>Amount:</label>
          <input type="number"  placeholder="Request Amount" onChange={(e) => setAmount(e.target.value)}></input>
        </div>
        <div className="field">
          <label>Recipient:</label>
          <input type="text" name="Minimum Contribution" placeholder="Payable Address" onChange={(e) => setRecp(e.target.value)}></input>
        </div>
      </form>
      
      <p></p>
      <button className='ui button blue' onClick >Create Request</button>
      <h2>Request Info:</h2>
      <Link to="./showrequest"></Link>
      <Router>
        <Switch>
        <Route path="./showrequest" component={showRequest}>
          
        </Route>
        </Switch>
        </Router>       
        
        
         </div>
  
  );
}
export default Tender
