import React, { Component } from 'react';
import { BrowserRouter as Router,Link,Switch,Route } from 'react-router-dom';
import Guideline from '../dashComponents/Guideline';
import Donation from '../dashComponents/Donation';
import Tender from '../dashComponents/Tender';
import '../dashComponents/sidebar.css';
import aboutUS from '../dashComponents/aboutUS';
import RequestLog from '../dashComponents/RequestLog';

 class Dashboard extends Component {
  state = { web3: null,
    account: null,
    contract: null,
    sidebar : true
 };
 componentDidMount = async () => {
   try {
     // Get network provider and web3 instance.
     const web3 = await getWeb3();

     // Use web3 to get the user's accounts.
     const accounts = await web3.eth.getAccounts();

     // Get the BCS contract instance.
     const networkId = await web3.eth.net.getId();
     const deployedNetwork = bcs.networks[networkId];
     const contractA = new web3.eth.Contract(
       bcs.abi,
       deployedNetwork && deployedNetwork.address,
     );
     


     // Set web3, accounts, and contract to the state, and then proceed with an
     // example of interacting with the contract's methods.
     this.setState({ web3, account:accounts[0], contract: contractA},this.start);
   } catch (error) {
     // Catch any errors for any of the above operations.
     alert(
       `Failed to load web3, accounts, or contract. Check console for details.`,
     );
     console.error(error);
   }

 };
 start = async () => {
  
   const { web3,account,contract } = this.state;
   
   console.log("web3 =", web3);
   console.log("Contract =", contract);
   console.log("Acoount =", account);
 };

     
      

    
  showSidebar = () => { 
    console.log("contract=",this.state.contract)
     this.setState({sidebar: !(this.state.sidebar)});

  }
  render() {
    return( 
    <div>

    <Router>
    <nav className={this.state.sidebar ? "sidebar active" : "sidebar"}>
      <button className="hamburger" type="button" onClick={this.showSidebar}><div></div></button>
      <ul onClick={this.showSidebar}>
        <li>DASHBOARD</li>
        <li><Link to="/dashboard">User Guideline</Link></li>
        <li><Link to="/dashboard/tender">Tender Logs</Link></li>
        <li><Link to="/dashboard/donation">Donation Logs</Link></li>
        <li><Link to="/dashboard">Request Logs</Link></li>
        <li><Link to="/dashboard/aboutus">About Us</Link></li>
      </ul>
    </nav>
    

    <Switch>
      
      <Route path="/dashboard" exact component={Guideline} />

      <Route path="/dashboard/tender">
        <Tender 
         web3={this.state.web3}
         account={this.state.account}
         contract={this.state.contract} />
      </Route>

      <Route path="/dashboard/donation">
      <Donation
         web3={this.state.web3}
         account={this.state.account}
         contract={this.state.contract} />
      </Route>

      <Route path="/dashboard/requestlog">
        <RequestLog 
        web3={this.state.web3}
        contract={this.state.contract}
        account={this.state.account}/>
      </Route>

      <Route path="/dashboard/aboutus"  component={aboutUS} />

    </Switch>
    </Router>

  </div>
    );
  }
}
export default Dashboard
