import React, { Component } from "react";
import Authentication from "./contracts/Authentication.json";
import getWeb3 from "./getWeb3";
import dashBoard from "../components/dashBoard";
import 'semantic-ui-css/semantic.min.css';

import "./App.css";

class App extends Component {
  state = { web3: null, account: null, contract: null };

  componentDidMount = async () => {
    try {
      // Get network provider and web3 instance.
      const web3 = await getWeb3();

      // Use web3 to get the user's accounts.
      const accounts = await web3.eth.getAccounts();

      // Get the contract instance.
      const networkId = await web3.eth.net.getId();
      const deployedNetwork = Authentication.networks[networkId];
      const contract1 = new web3.eth.Contract(
        Authentication.abi,
        deployedNetwork && deployedNetwork.address,
      );

      // Set web3, accounts, and contract to the state, and then proceed with an
      // example of interacting with the contract's methods.
      this.setState({ web3, account:accounts[0], contract: contract1 },this.start);
    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Failed to load web3, accounts, or contract. Check console for details.`,
      );
      console.error(error);
    }

  };
  start = async () => {

    const { web3,account,contract } = this.state;

    console.log("web3 =", web3);
    console.log("Contract =", contract);
    console.log("Acoount =", account);
  };




  render() {
    if (!this.state.web3) {
      return <div>Loading Web3, accounts, and contract...</div>;
    }
    return (
      <div>
        <h1>this is tender page</h1>
     <dashBoard></dashBoard>
      </div>
    );
  }
}

export default App;
