import React, {useState,useEffect} from 'react';
import { Table,Checkbox,Button} from 'semantic-ui-react';
import '../App.css'


const showRequest=(props) =>{
 
   const[reqNo,setReq]=useState(null)
   const[cAddress,setCaddress]=useState(null)
   const[description,setDes]=useState(null)
   const[amount,setAmount]=useState(null)
   const[recipient,setRecp]=useState(null)
   const[donors,setDonors]=useState(null)
   const[voters,setVoters]=useState(null)
   const[completed,setCom]=useState(null)

   

 const requestArray={
   reqNo,cAddress,description,amount,recipient,donors,voters,completed
 }
  const[Requests,setRequests]=useState([])
  
  
  
    return (
      <div className="ui container">
      <h2>Request Info:</h2>
      <Table compact celled definition>
    <Table.Header>
      <Table.Row>
        <Table.HeaderCell />
        <Table.HeaderCell>Request_NO</Table.HeaderCell>
        <Table.HeaderCell>Creator</Table.HeaderCell>
        <Table.HeaderCell>Description:</Table.HeaderCell>
        <Table.HeaderCell>Amount:</Table.HeaderCell>
        <Table.HeaderCell>Recipient:</Table.HeaderCell>
        <Table.HeaderCell>Voters/Donors</Table.HeaderCell>
       
      </Table.Row>
    </Table.Header>
     

     {Requests.map((request,index) => (
        <Table.Body>
        <Table.Row key={index}>
          <Table.Cell collapsing>
            <Checkbox slider />
          </Table.Cell>
          <Table.Cell>{reqNo}</Table.Cell>
          <Table.Cell>{cAddress}</Table.Cell>
          <Table.Cell>{description}</Table.Cell>
          <Table.Cell>{amount}</Table.Cell>
          <Table.Cell>{recipient}</Table.Cell>
          <Table.Cell>{voters}/{donors}</Table.Cell>
          
        </Table.Row>
        </Table.Body>
        

     )
     )}
      <Table.Footer fullWidth>
      <Table.Row>
        <Table.HeaderCell />
        <Table.HeaderCell colSpan='6'>
          <Button
            floated='right'
            icon
            labelPosition='left'
            primary
            size='small'
          >
          Make Payment
          </Button>
          <Button size='small' color="green">Vote</Button>
        </Table.HeaderCell>
      </Table.Row>
    </Table.Footer>

    </Table>

   </div> 




    );
  
}
export default showRequest
